DELETE FROM company;
DELETE FROM industry;



-- get zodiac signs for companies
CREATE TEMPORARY TABLE temp_company_sign (
    id        SERIAL PRIMARY KEY,
    month     INTEGER,
    day       INTEGER,    
    sign_id   INTEGER
);

-- fill temporary table with month and day of list date
SELECT id,
cast (split_part(text(split_part(text(list_date), ' ', 1)), '-', 2) as INTEGER),
cast (split_part(text(split_part(text(list_date), ' ', 1)), '-', 3) as INTEGER)
FROM company;

-- set company sign id
UPDATE company
SET sign_id=company_sign.sign_id
FROM (
	select t.id, s.id as sign_id 
	from temp_company_sign t 
	inner join sign s on (s.start_month = t.month and s.start_day <= t.day) 
						or (s.end_month = t.month and s.end_day >= t.day)) as company_sign
WHERE company.id=company_sign.id;

-- drop temporary table
DROP TABLE temp_company_sign;
